/**
 * Home Page Section Anchor Navigation
 * Handles smooth scrolling to sections on home page and redirects from other pages
 */
document.addEventListener('DOMContentLoaded',function(){const e=document.querySelectorAll('a[href^="#"]');e.forEach(e=>{e.addEventListener('click',function(t){const n=this.getAttribute('href');if("#"!==n&&n.startsWith('#')){const c=n.substring(1),o=null!==document.querySelector('#hero-sec');if(o)t.preventDefault(),document.getElementById(c)&&document.getElementById(c).scrollIntoView({behavior:'smooth',block:'start'}),history.pushState(null,null,n);else t.preventDefault(),window.location.href="/"+n}})}),window.location.hash&&(null!==document.querySelector('#hero-sec')&&setTimeout(()=>{const e=document.getElementById(window.location.hash.substring(1));e&&e.scrollIntoView({behavior:'smooth',block:'start'})},100)),window.addEventListener('hashchange',function(){window.location.hash&&(document.getElementById(window.location.hash.substring(1))&&document.getElementById(window.location.hash.substring(1)).scrollIntoView({behavior:'smooth',block:'start'}))})});
